import React, {useEffect, useContext} from 'react';
import RestaurantFinder from "../apis/RestaurantFinder";
import { RestaurantsContext } from '../context/RestaurantsContext';

const RestaurantList = (props) => {

    const {restaurants, setRestaurants}= useContext(RestaurantsContext)

    useEffect(() =>{

        const fetchData = async () => {
            
        try {
            const response = await RestaurantFinder.get("/")
            setRestaurants(response.data.data.restaurants);
        } catch (err) {}

        }

        fetchData();

    }, []);


    const handleDelete = async (code) => {

        try {
            const response = await RestaurantFinder.delete(`/${code}`);
            setRestaurants(restaurants.filter(restaurant => {
                return restaurant.code !== code
            }));
        } catch (err) {}

    }


    return (
        <div className="list-group">

            <table className="table table-hover">
                <thead>
                    <tr className="bg-primary">
                        <th scope="col">CODE</th>
                        <th scope="col">NAME</th>
                        <th scope="col">VALUE</th>
                        <th scope="col">STRAIN</th>
                        <th scope="col">WEIGHT</th>
                        <th scope="col">AUTHORS</th>
                        <th scope="col">NUMBER</th>
                        <th scope="col">PAGE</th>
                        <th scope="col">DATE</th>
                        <th scope="col">EDIT</th>
                        <th scope="col">CLEAR</th>
                    </tr>
                </thead>

                <tbody>

                    {restaurants && restaurants.map((restaurant) => {
                        return (
                        <tr key={restaurant.code}>
                            <td>{restaurant.code}</td>
                            <td>{restaurant.name}</td>
                            <td>{restaurant.value}</td>
                            <td>{restaurant.strain}</td>
                            <td>{restaurant.weight}</td>
                            <td>{restaurant.authors}</td>
                            <td>{restaurant.number}</td>
                            <td>{restaurant.page}</td>
                            <td>{restaurant.date}</td>
                            <td><button className="btn btn-warning">EDIT</button></td>
                            <td><button onClick={ () => handleDelete(restaurant.code)} className="btn btn-danger">DELETE</button></td>
                        </tr>
                        );
                    })}

                   {/* <tr>
                        <td>6128</td>
                        <td>Methylstyrol</td>
                        <td>-4.52</td>
                        <td>Human</td>
                        <td>118.18</td>
                        <td>J.Health</td>
                        <td>46</td>
                        <td>282</td>
                        <td>2000</td>
                        <td><button className="btn btn-warning">EDIT</button></td>
                        <td><button className="btn btn-danger">DELETE</button></td>
                    </tr> */ }
                </tbody>

            </table>
            
        </div>
    )
}

export default RestaurantList
