import React, {useContext, useState} from 'react';
import RestaurantFinder from "../apis/RestaurantFinder"
import { RestaurantsContext } from '../context/RestaurantsContext';

const AddRestaurant = () => {

    const {AddRestaurants} = useContext(RestaurantsContext);

    const [name, setName] = useState ("");
    const [value, setValue] = useState ("");
    const [strain, setStrain] = useState ("");
    const [weight, setWeight] = useState ("");
    const [authors, setAuthors] = useState ("");
    const [number, setNumber] = useState ("");
    const [page, setPage] = useState ("");
    const [date, setDate] = useState ("");

    const handleSubmit = async (e) => {
        e.preventDefault()
        try {
            const response = await RestaurantFinder.post("/",{
                name,
                value,
                strain,
                weight,
                authors,
                number,
                page,
                date,
            });

            AddRestaurants(response.data.data.restaurant);

        } catch (err) {}
    };



    return (
        <div className="mb-4">
            <form action="">
                
                <div className="container">

                    <div className="row">

                        <div className="col-sm">
                            <input value={name} onChange={(e) => setName(e.target.value)} type="text" className="form-control" placeholder="name"/>
                        </div>
                        <div className="col-sm">
                            <input value={value} onChange={(e) => setValue(e.target.value)} className="form-control" type="float" placeholder="Value"/>
                        </div>
                        <div className="col-sm">
                            <select value={strain} onChange={(e) => setStrain(e.target.value)} className="custom-select my-1 mr-sm-2">
                                <option disabled>Strain</option>
                                <option value="1">HUMAN</option>
                                <option value="2">RAT</option>
                                <option value="3">MOUSE</option>
                                <option value="4">CHICKEN</option>
                                <option value="5">LIZARD</option>
                            </select>
                        </div>
                        <div className="col-sm">
                            <input value={weight} onChange={(e) => setWeight(e.target.value)} className="form-control" type="float" placeholder="Weight"/>
                        </div>
                        <div className="col-sm">
                            <input value={authors} onChange={(e) => setAuthors(e.target.value)} className="form-control" type="text" placeholder="Authors"/>
                        </div>
                        <div className="col-sm">
                            <input value={number} onChange={(e) => setNumber(e.target.value)} className="form-control" type="int" placeholder="Number"/>
                        </div>
                        <div className="col-sm">
                            <input value={page} onChange={(e) => setPage(e.target.value)} className="form-control" type="int" placeholder="Page"/>
                        </div>
                        <div className="col-sm">
                            <input value={date} onChange={(e) => setDate(e.target.value)} className="form-control" type="int" placeholder="Date"/>
                        </div>
                        <button onClick={handleSubmit} type="submit" className="btn btn-primary col-sm">ADD</button>

                    </div>

                </div>

            </form>
        </div>
    );
};

export default AddRestaurant;
