import React, {useState, useContext, useEffect} from 'react';
import { useHistory, useParams } from 'react-router'
import RestaurantFinder from '../apis/RestaurantFinder';

const UpdateRestaurant = (props) => {

    const {code} = useParams ();
    let history = useHistory()

    const [name, setName] = useState("");
    const [value, setValue] = useState ("");
    const [strain, setStrain] = useState ("");
    const [weight, setWeight] = useState ("");
    const [authors, setAuthors] = useState ("");
    const [number, setNumber] = useState ("");
    const [page, setPage] = useState ("");
    const [date, setDate] = useState ("");

    useEffect(() => {
        const fetchData = async () => {
            const response = await RestaurantFinder.get(`/${code}`);
            setName(response.data.data.restaurant.name)
            setValue(response.data.data.restaurant.value)
            setStrain(response.data.data.restaurant.strain)
            setWeight(response.data.data.restaurant.weight)
            setAuthors(response.data.data.restaurant.authors)
            setNumber(response.data.data.restaurant.number)
            setPage(response.data.data.restaurant.page)
            setDate(response.data.data.restaurant.date)
        };

        fetchData();
    }, []);


    const handleSubmit = async(e) => {
        e.preventDefault()
        const updatedRestaurant = await RestaurantFinder.put(`/${code}`, {
            name,
            value,
            strain,
            weight,
            authors,
            number,
            page,
            date,
        });
        history.push("/")
    };


    return (
        <div>

            <form action="">

                <div className="form-group">
                    <label htmlFor="name">Name</label>
                    <input value={name} onChange={(e) => setName(e.target.value)} id="name" className="form-control" type="text" />
                </div>

                <div className="form-group">
                    <label htmlFor="Value">Value</label>
                    <input value={value} onChange={(e) => setValue(e.target.value)} id="Value" className="form-control" type="float" />
                </div>

                <div className="form-group">
                    <label htmlFor="Strain">Strain</label>
                    <input value={strain} onChange={(e) => setStrain(e.target.value)} id="Strain" className="form-control" type="text" />
                </div>

                <div className="form-group">
                    <label htmlFor="Weight">Weight</label>
                    <input value={weight} onChange={(e) => setWeight(e.target.value)} id="Weight" className="form-control" type="float" />
                </div>

                <div className="form-group">
                    <label htmlFor="Author">Author</label>
                    <input value={authors} onChange={(e) => setAuthors(e.target.value)} id="Author" className="form-control" type="text" />
                </div>

                <div className="form-group">
                    <label htmlFor="Number">Number</label>
                    <input value={number} onChange={(e) => setNumber(e.target.value)} id="Number" className="form-control" type="number" />
                </div>

                <div className="form-group">
                    <label htmlFor="Page">Page</label>
                    <input value={page} onChange={(e) => setPage(e.target.value)} id="Page" className="form-control" type="number" />
                </div>

                <div className="form-group">
                    <label htmlFor="date">Date</label>
                    <input value={date} onChange={(e) => setDate(e.target.value)} id="date" className="form-control" type="number" />
                </div>

                <button type="submit" onClick={handleSubmit} className="btn btn-primary">Submit</button>

            </form> 
        </div>
    )
}

export default UpdateRestaurant
